&lt;p&gt;环境：blindxxe.php&lt;/p&gt;
&lt;figure class=&#34;highlight plaintext&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&amp;lt;?php&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;$string_xml = &amp;#x27;&amp;lt;?xml version=&amp;quot;1.0&amp;quot;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;encoding=&amp;quot;GB18030&amp;quot;?&amp;gt;&amp;lt;note&amp;gt;&amp;lt;to&amp;gt;George&amp;lt;/to&amp;gt;&amp;lt;from&amp;gt;John&amp;lt;/from&amp;gt;&amp;lt;heading&amp;gt;Reminder&amp;lt;/heading&amp;gt;&amp;lt;body&amp;gt;xml&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;实体注入&amp;lt;/body&amp;gt;&amp;lt;/note&amp;gt;&amp;#x27;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;$xml = isset($_GET[&amp;#x27;xml&amp;#x27;])?$_GET[&amp;#x27;xml&amp;#x27;]:$string_xml;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;$data = simplexml_load_string($xml);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;echo &amp;#x27;&amp;lt;meta charset=&amp;quot;GB18030&amp;quot;&amp;gt;&amp;#x27;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;?&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
